package ch04;

public class gugudan2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10;
		int num2 = 2;
		String oper = "/";
		
		if (oper  == "+") {
			System.out.println(num1+num2);
		}
		else if (oper  == "-") {
			System.out.println(num1-num2);
		}
		else if (oper  == "*") {
			System.out.println(num1*num2);
		}
		else if (oper  == "/") {
			System.out.println(num1/num2);
		}
	
	
	}
}
