package ch04;

public class gugudan3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10;
		int num2 = 2;
		String oper = "/";
		
		switch(oper){
		case "+" : 
			System.out.println(num1+num2);
		case "-" : 
			System.out.println(num1-num2);
		case "*" : 
			System.out.println(num1*num2);
		case "/" : 
			System.out.println(num1/num2);
		}
	}
}
